INSERT INTO books (isbn, title, author, publisher, status, description) VALUES (9788375746068, 'Pan Lodowego Ogrodu (tom 1)', 'Jarosław Grzędowicz', 'Fabryka Słów', 'AVAILABLE', 'Pan z Wami! Jako i ogród jego! Wstąpiwszy, porzućcie nadzieję. Oślepną monitory, ogłuchną komunikatory, zamilknie broń. Tu włada magia.\nVuko Drakkainen samotnie rusza na ratunek ekspedycji naukowej badającej człekopodobną cywilizację planety Midgaard. Pod żadnym pozorem nie może ingerować w rozwój nieznanej kultury. Trafia na zły czas. Planeta powitała go mgłą i śmiercią. Dalej jest tylko gorzej. Trwa wojna bogów. Giną śmiertelnicy. Odwieczne reguły zostały złamane.');
